    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String email = campo_email.getText();
        char[] charPassword = jPasswordField1.getPassword();
        String password = new String(charPassword);
        
        if (DB_Login.isUserPassword(email, password)) {
            if (DB_Login.isUserActive(email)) {
                JOptionPane.showMessageDialog(this, "Datos correctos. Usuario activo. \nPerfil: " + DB_Login.getUserPerfil(email));
            } else {
                JOptionPane.showMessageDialog(this, "Datos correctos. Usuario inactivo. \nPerfil: " + DB_Login.getUserPerfil(email));
            }
        } else {
            JOptionPane.showMessageDialog(this, "Los datos introducidos son incorrectos.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

